<template>
  <img ref="lazyImage" decoding="async" :alt="alt">
</template>
<style scoped>
img {
  width: 100%;
  height: 100%;
  background-image: url("data:image/jpeg;base64,");
  background-size: cover;
  background-repeat: no-repeat;
  display: block;
  opacity: 0;
  transition: opacity 250ms ease-in;
  object-fit: cover;
  height: 100%;
}
img.loaded {
  opacity: 1;
}
</style>

<script lang="ts">
import { Component, Vue, Prop } from "vue-property-decorator";

@Component({})
export default class LazyImg extends Vue {
  @Prop(String)
  lazySrc!: string;

  alt: string = "";
  observer: IntersectionObserver | null = null;

  mounted() {

    if ("IntersectionObserver" in window) {
      this.observer = new IntersectionObserver(
        this.onObserve.bind(this),
      );
      this.observer.observe(this.$el as Element);
    } else {
      setTimeout(() => this.preload(this.$refs.lazyImage as Element), 200);
    }
  }

  onObserve(data: IntersectionObserverEntry[]) {
    if (data[0].isIntersecting) {
      // console.log('intersection')
      this.preload(data[0].target).then(() => {
        if (this.observer) this.observer.disconnect();
      });
    }
    return;
  }

  applyImage(target: HTMLImageElement, src: string) {
    return new Promise(resolve => {
      target.src = src;
      resolve();
    });
  }

  fetchImage(url: string) {
    return new Promise((resolve, reject) => {
      const image = new Image();
      image.src = url;
      image.onload = resolve;
      image.onerror = reject;
    });
  }

  preload(targetEl: Element) {
    return this.fetchImage(this.lazySrc)
      .then(() => this.applyImage(targetEl as HTMLImageElement, this.lazySrc))
      .then(() =>
        (this.$refs.lazyImage as HTMLImageElement).classList.add("loaded")
      );
  }
}
</script>
